Solution.Query:
  PREFIX zb: <https://zbmath.org/>
  SELECT DISTINCT ?publication
  WHERE {
      ?publication zb:classification ?cls1 .
FILTER(CONTAINS(STR(?cls1), "https://zbmath.org/classification/?q=cc%3A28A35"))
  }

  PREFIX zb: <https://zbmath.org/>
  SELECT DISTINCT ?publication
  WHERE {
      ?publication zb:classification ?cls1 .
FILTER(CONTAINS(STR(?cls1), "https://zbmath.org/classification/?q=cc%3A22D99"))
  }
